// This program increments and decrements a number and prints the results.
// Initializing the number
let num = 10;
// Incrementing the number
num++; // num is now 11
console.log("After incrementing, the number is: " + num);
// Decrementing the number
num--; // num is now 10 again
console.log("After decrementing, the number is: " + num);
